import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [player, setPlayer] = useState("");
  const [playerData, setPlayerData] = useState({});
  const API_KEY = "RGAPI-e7499229-e51a-4fb5-b066-74749ec7d81e";

  function searchPlayer(event) {
    // APi string setup
    const APICallString = `https://eun1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${player}?api_key=${API_KEY}`;
    // Handle API call
    axios.get(APICallString).then(function (response) {
      // Success
      setPlayerData(response.data);
    }).catch(function (error) {
      // Error
      console.log(error);
    })
  }

  return (
    <div className="App">
      <h1>League Stats</h1>
      <input type="text" onChange={e => setPlayer(e.target.value)}></input>
      <button onClick={e => searchPlayer(e)}>Search</button>
      {JSON.stringify(playerData) !== '{}' ? 
        <div style={{border: "2px solid red"}}>
          <p>{playerData.name}</p>
          <img width="100" height="100" src={`http://ddragon.leagueoflegends.com/cdn/13.8.1/img/profileicon/${playerData.profileIconId}.png`} />
          <p>Level: {playerData.summonerLevel}</p>
        </div>
       : 
        <><p>No player data</p></>}
    </div>
  );
}

export default App;
