import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [player, setPlayer] = useState("");
  const [playerData, setPlayerData] = useState({});
  const [matchHistory, setMatchHistory] = useState([]);
  const API_KEY = "RGAPI-a2615567-46a6-419c-9506-165cb9a6a1f3";

  function searchPlayer(event) {
    // APi string setup
    const APICallString_Player = `https://eun1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${player}?api_key=${API_KEY}`;
    // Handle API call
    axios.get(APICallString_Player).then(function (response) {
      // Success
      setPlayerData(response.data);
      console.log(response.data);
    }).catch(function (error) {
      // Error
      console.log(error);
    })
    // API call to backend for match history
    axios.get("http://localhost:5000/", { params: { username: player}})
      .then(function (response) {
        setMatchHistory(response.data)
      }).catch(function (error) {
        console.log(error);
      })
      console.log(matchHistory);
  }

  function searchUser(event) {
    axios.get("http://localhost:5000/", { params: { username: player}})
      .then(function (response) {
        setMatchHistory(response.data)
        setPlayerData(response.data)
      }).catch(function (error) {
        console.log(error);
      })
      console.log(matchHistory);
    }


  return (
    <div className="App">
      <h1>League Stats</h1>
      <input type="text" onChange={e => setPlayer(e.target.value)}></input>
      <button onClick={e => searchPlayer(e)}>Search</button>
      {JSON.stringify(playerData) !== '{}' ? 
        <div className="container">
          <div className="playerProfile" style={{border: "5px solid red"}}>
            <p>{playerData.name}</p>
            <img width="100" height="100" src={`http://ddragon.leagueoflegends.com/cdn/13.8.1/img/profileicon/${playerData.profileIconId}.png`} />
            <p>Level: {playerData.summonerLevel}</p>
          </div>
          {matchHistory.length !== 0 ?
            <div className="matchHistory" style={{border: "5px solid red"}}>
              Match History
              {matchHistory.map((gameData, index) => 
                <div>
                  <h2>Game {index + 1}</h2>
                  <div>
                    {gameData.info.participants.map((data, participantIndex) => 
                      <div>
                        <p>
                          PLAYER {participantIndex + 1}: {data.summonerName}, KDA: {data.kills} / {data.deaths} / {data.assists}
                        </p>

                      </div>
                      // <p>
                      //   {participantIndex < 5 ? `${data.summonerName} ${data.kills}/${data.deaths}/${data.assists}` : <>eee</>}
                      // </p>
                    )}
                  </div>
                </div>
              )}
            </div>
            : "No games to show"
          }
        </div>
       : 
        <><p>No player data</p></>}
    </div>
  );
}

export default App;
